import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const EditCustomer = (customer, setCustomer) => {
  const [editingCustomer, setEditingCustomer] = useState(customer);
  const { id } = useParams();

  useEffect(() => {
    axios.get(`http://localhost:5000/customers/${id}`).then((customer) => {
      setEditingCustomer(customer.data);
    });
  });

  const updateCustomer = () => {
    console.log(customer);
    axios
      .patch(`http://localhost:5000/customers/${id}`, customer)
      .then((customer) => {
        setEditingCustomer(customer.data);
        window.alert("Successfully Edited");
      });
  };
  return (
    <>
      <h2>Update Customer Details</h2>

      <form onSubmit={updateCustomer}>
        <label>
          Name:
          <input
            type="text"
            value={editingCustomer.name}
            onChange={(e) =>
              setEditingCustomer({ ...customer, name: e.target.value })
            }
          />
        </label>

        <label>
          Email:
          <input
            type="text"
            onChange={(e) =>
              setEditingCustomer({ ...customer, email: e.target.value })
            }
          />
        </label>

        <label>
          Phone number:
          <input
            type="text"
            onChange={(e) =>
              setEditingCustomer({ ...customer, phoneNumber: e.target.value })
            }
          />
        </label>

        <label>
          Location:
          <input
            type="text"
            onChange={(e) =>
              setEditingCustomer({ ...customer, location: e.target.value })
            }
          />
        </label>

        <label>
          Sub pack:
          <input
            type="text"
            onChange={(e) =>
              setEditingCustomer({
                ...customer,
                subscribedToPackage: e.target.value,
              })
            }
          />
        </label>

        <input type="submit" value="Submit" />
      </form>
    </>
  );
};

export default EditCustomer;
