import React from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import { Link } from "react-router-dom";

import "swiper/css";

import "swiper/css/effect-coverflow";
import "swiper/css/pagination";

import { EffectCoverflow, Pagination } from "swiper";

const Home = () => {
  const lientest =
    "https://www.livredepoche.com/sites/default/files/images/livres/couv/9782253006329-001-T.jpeg";
  return (
    <>
      <Link to={"/bookList"}>Liste</Link>
      <Swiper
        //loop={true}
        effect={"coverflow"}
        grabCursor={true}
        centeredSlides={true}
        slidesPerView={3}
        coverflowEffect={{
          rotate: 50,
          stretch: 0,
          depth: 100,
          modifier: 1,
          slideShadows: true,
        }}
        pagination={true}
        modules={[EffectCoverflow, Pagination]}
        className="mySwiper"
      >
        <SwiperSlide>
          <img src="/booksCover/atalarene.jpeg" alt="atala" />
        </SwiperSlide>
        <SwiperSlide>
          <img src="/booksCover/LaConfession.jpeg" alt="confession" />
        </SwiperSlide>

        <SwiperSlide>
          <img src="/booksCover/GuerreEtPaix.jpeg" alt="guerre" />
        </SwiperSlide>
        <SwiperSlide>
          <img src="/booksCover/crimeetchatiment.jpeg" alt="crime" />
        </SwiperSlide>
        <SwiperSlide>
          <img src="/booksCover/michelstrogoff.jpeg" alt="michel" />
        </SwiperSlide>
        <SwiperSlide>
          <img src="/booksCover/vingtmille.jpeg" alt="vingtmille" />
        </SwiperSlide>
        <SwiperSlide>
          <img src={lientest} alt="vingtmille" />
        </SwiperSlide>
      </Swiper>
    </>
  );
};

export default Home;
