import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function BookList() {
  const [bookList, setBookList] = useState([]);

  const deleteBook = (id) => {
    axios.delete(`http://localhost:5000/books/${id}`).then(() => {
      window.location.reload(false);
    });
  };

  useEffect(() => {
    axios.get('http://localhost:5000/books').then((allBooks) => {
      setBookList(allBooks.data.filter((book) => book.status !== 'À lire'));
    });
  }, []);

  return (
    <table className="mx-auto">
      <thead className="bg-white border-b">
        <tr>
          <th>Title</th>
          <th>Author</th>
          <th>Parution</th>
          <th>Country</th>
          <th>reading</th>
          <th>cover</th>
          <th>status</th>
          <th>actions</th>
        </tr>
      </thead>
      <tbody>
        {bookList.map((book) => (
          <tr
            className="bg-white border-b transition duration-300 ease-in-out hover:bg-gray-100 mx-8"
            key={book.id}
          >
            <td>{book.title}</td>
            <td>{book.author}</td>
            <td>{book.parution}</td>
            <td>{book.country}</td>
            <td>{book.reading}</td>
            <td>
              <img src={book.cover} alt="" className="h-28" />
            </td>
            <td>{book.status}</td>

            <td>
              <button
                type="button"
                className="bg-transparent hover:bg-blue-500 text-blue-700 font-semibold hover:text-white py-2 px-4 border border-blue-500 hover:border-transparent rounded"
              >
                <Link to={`/edit/${book.id}`}>Edit</Link>
              </button>

              <button
                type="button"
                className="bg-transparent hover:bg-blue-500 text-blue-700 font-semibold hover:text-white py-2 px-4 border border-blue-500 hover:border-transparent rounded"
                onClick={() => deleteBook(book.id)}
              >
                Delete
              </button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}

export default BookList;
