import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const CustomerList = () => {
  const [customerList, showCustomerList] = useState([]);

  const deleteCustomer = (id) => {
    axios.delete(`http://localhost:5000/customers/${id}`).then(() => {
      window.location.reload(false);
    });
  };

  useEffect(() => {
    axios.get("http://localhost:5000/customers").then((allCustomers) => {
      showCustomerList(allCustomers.data);
    });
  }, []);

  return (
    <>
      <h1>all customers</h1>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>email</th>
            <th>phone</th>
            <th>location</th>
            <th>sub pack</th>
            <th>action</th>
          </tr>
        </thead>
        <tbody>
          {customerList.map((customer, key) => (
            <tr key={customer.key}>
              <td>{customer.name}</td>
              <td>{customer.email}</td>
              <td>{customer.phoneNumber}</td>
              <td>{customer.location}</td>
              <td>{customer.subscribedToPackage}</td>
              <td component={Link} to={`/edit/${customer._id}`}>
                Edit
              </td>
              <td onClick={() => deleteCustomer(customer._id)}>Delete</td>
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
};

export default CustomerList;
