import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const Statistics = () => {
  const [bookList, setBookList] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/books").then((allBooks) => {
      setBookList(allBooks.data);
    });
  }, []);

  const countries = bookList
    .map((book) => book.country)
    .filter(
      (item, index) =>
        bookList.map((book) => book.country).indexOf(item) === index
    );

  console.log(
    bookList
      .map((book) => book.country)
      .reduce((sum, country) => (country === "Russie" ? sum + 1 : sum), 0),
    "Russie",
    countries[0]
  );

  return (
    <>
      <Link to={"/bookList"}>Liste</Link>
      <div>
        {countries.map((country) => (
          <h1>Nombre d'oeuvres provenant de {country}: </h1>
        ))}
      </div>
    </>
  );
};

export default Statistics;
