import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const Statistics = () => {
  const [bookList, setBookList] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/books").then((allBooks) => {
      setBookList(allBooks.data);
    });
  }, []);

  console.log(
    bookList
      .map((book) => book.country)
      .reduce((sum, country) => (country === "Russie" ? sum + 1 : sum), 0),
    Russie
  );

  const countries = bookList
    .map((book) => book.country)
    .filter(
      (item, index) =>
        bookList.map((book) => book.country).indexOf(item) === index
    );

  console.log(countries);

  return (
    <>
      <Link to={"/bookList"}>Liste</Link>
      <div>
        {countries.map((countryy) => (
          <h1>
            Nombre d'oeuvres provenant de {countryy}:{" "}
            {bookList
              .map((book) => book.country)
              .reduce(
                (sum, index) =>
                  index === String({ countryy }) ? sum + 1 : sum,
                0
              )}
          </h1>
        ))}
      </div>
    </>
  );
};

export default Statistics;
