  const location = useLocation();
  useEffect(() => {
    console.log(location);
  });
  return (
    <div className="flex flex-row justify-between px-36 bg-cyan-700 text-white font-bold py-2">
      <Link to={"/"} className={location.pathname === "/" ? "underline" : ""}>
        Accueil
      </Link>
      <div>Activit√©s</div>
      <Link
        to={"/bookList"}
        className={location.pathname === "/bookList" ? "underline" : ""}
      >
        Listes
      </Link>
      <Link
        to={"/stats"}
        className={location.pathname === "/stats" ? "underline" : ""}
      >
        Statistiques
      </Link>
    </div>
  );
};

export default NavBar;
