import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const Statistics = () => {
  const [bookList, setBookList] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/books").then((allBooks) => {
      setBookList(allBooks.data);
    });
  }, []);

  console.log(
    bookList
      .map((book) => book.country)
      .reduce((sum, country) => (country === "Russie" ? sum + 1 : sum), 0)
  );

  const countries = bookList
    .map((book) => book.country)
    .filter(
      (item, index) =>
        bookList.map((book) => book.country).indexOf(item) === index
    );

  console.log(countries);

  return (
    <>
      <Link to={"/bookList"}>Liste</Link>
      <h1>
        Origine des livres:{" "}
        {bookList
          .map((book) => book.country)
          .filter(
            (item, index) =>
              bookList.map((book) => book.country).indexOf(item) === index
          )}
      </h1>
      {bookList.map((book, key) => (
        <tr key={key}>
          <td>{book.title}</td>
          <td>{book.author}</td>
          <td>{book.parution}</td>
          <td>{book.country}</td>
          <td>{book.genre}</td>
          <td>{book.moviment}</td>
          <td>{book.reading}</td>
          <td>
            <img src={book.cover} alt="" className="h-28" />
          </td>
          <td>
            <Link to={`/edit/${book._id}`}>Edit</Link>
          </td>
          <td onClick={() => deleteBook(book._id)}>Delete</td>
        </tr>
      ))}
    </>
  );
};

export default Statistics;
