import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const Statistics = () => {
  const [bookList, setBookList] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/books").then((allBooks) => {
      setBookList(allBooks.data);
    });
  }, []);

  const countriesList = bookList
    .map((book) => book.country)
    .filter(
      (item, index) =>
        bookList.map((book) => book.country).indexOf(item) === index
    )
    .sort();

  const authorsList = bookList
    .map((book) => book.author)
    .filter(
      (item, index) =>
        bookList.map((book) => book.author).indexOf(item) === index
    )
    .sort();

  const centuriesList = bookList
    .map((book) => book.parution)
    .sort()
    .map((element) => Math.ceil(element / 100))
    .filter(
      (item, index) =>
        bookList
          .map((book) => book.parution)
          .sort()
          .map((element) => Math.ceil(element / 100))
          .indexOf(item) === index
    );

  return (
    <>
      <Link to={"/bookList"}>Liste</Link>
      <div>
        {countriesList.map((country) => (
          <h1>
            Nombre d'oeuvres provenant de {country}:{" "}
            {bookList
              .map((book) => book.country)
              .reduce((sum, member) => (member === country ? sum + 1 : sum), 0)}
          </h1>
        ))}
      </div>
      <br></br>
      <div>
        {authorsList.map((author) => (
          <h1>
            Nombre d'oeuvres provenant de {author}:{" "}
            {bookList
              .map((book) => book.author)
              .reduce((sum, member) => (member === author ? sum + 1 : sum), 0)}
          </h1>
        ))}
      </div>
      <br></br>
      <div>
        {centuriesList.map((century) => (
          <h1>
            Nombres d'oeuvres provenant du {century}e siÃ¨cle:{" "}
            {bookList
              .map((book) => book.parution)
              .map((element) => Math.ceil(element / 100))
              .reduce((sum, member) => (member === century ? sum + 1 : sum), 0)}
          </h1>
        ))}
      </div>
    </>
  );
};

export default Statistics;
