import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const EditCustomer = () => {
  const [customer, showCustomer] = useState([]);
  const { id } = useParams();

  useEffect(() => {
    axios.get(`http://localhost:5000/customers/${id}`).then((customer) => {
      showCustomer(customer.data);
    });
  });

  const updateCustomer = () => {
    axios
      .patch(`http://localhost:5000/customers/${id}`, customer)
      .then((customer) => {
        showCustomer(customer.data);
        window.alert("Successfully Edited");
      });
  };
  return (
    <>
      <h2>Update Customer Details</h2>

      <form onSubmit={(e) => this.onSubmit(e, customer._id)}>
        <label>
          Name:
          <input
            type="text"
            id="name"
            onChange={(e) =>
              showCustomer({ ...customer, name: e.target.value })
            }
          />
        </label>

        <label>
          Email:
          <input
            type="text"
            id="email"
            onChange={(e) =>
              showCustomer({ ...customer, email: e.target.value })
            }
          />
        </label>

        <label>
          Phone number:
          <input
            type="text"
            id="phoneNumber"
            onChange={(e) =>
              showCustomer({ ...customer, phoneNumber: e.target.value })
            }
          />
        </label>

        <label>
          Location:
          <input
            type="text"
            id="location"
            onChange={(e) =>
              showCustomer({ ...customer, location: e.target.value })
            }
          />
        </label>

        <label>
          Sub pack:
          <input
            type="text"
            id="subscription"
            onChange={(e) =>
              showCustomer({ ...customer, subscribedToPackage: e.target.value })
            }
          />
        </label>

        <input type="submit" value="Submit" />
      </form>
    </>
  );
};

export default EditCustomer;
