import React, { useState } from 'react';
import axios from 'axios';

function CreateBook() {
  const [book, setBook] = useState({
    title: '',
    author: '',
    parution: '',
    country: '',
    genre: '',
    moviment: '',
    reading: '',
    cover: '',
    status: '',
  });

  const createBook = () => {
    axios.post('http://localhost:5000/books', book).then(() => {
      window.location.reload(false);
    });
  };

  return (
    <>
      <h2 className="pt-12 mb-6">Ajouter un nouveau livre:</h2>
      <form onSubmit={createBook} className="flex flex-col gap-4">
        <label htmlFor="title">
          Titre *:
          <input
            id="title"
            type="text"
            value={book.title}
            onChange={(e) => setBook({ ...book, title: e.target.value })}
          />
        </label>

        <label htmlFor="author">
          Auteur *:
          <input
            id="author"
            placeholder="Nom de famille d'abord"
            type="text"
            value={book.author}
            onChange={(e) => setBook({ ...book, author: e.target.value })}
          />
        </label>

        <label htmlFor="parution">
          Parution *:
          <input
            id="parution"
            type="text"
            value={book.parution}
            onChange={(e) => setBook({ ...book, parution: e.target.value })}
          />
        </label>

        <label htmlFor="country">
          Pays *:
          <input
            id="country"
            type="text"
            value={book.country}
            onChange={(e) => setBook({ ...book, country: e.target.value })}
          />
        </label>

        <label htmlFor="genre">
          Genre
          <input
            id="genre"
            type="text"
            value={book.genre}
            onChange={(e) => setBook({ ...book, genre: e.target.value })}
          />
        </label>

        <label htmlFor="moviment">
          Mouvement
          <input
            id="moviment"
            type="text"
            value={book.moviment}
            onChange={(e) => setBook({ ...book, moviment: e.target.value })}
          />
        </label>

        <label htmlFor="reading">
          Lecture
          <input
            id="reading"
            type="text"
            value={book.reading}
            onChange={(e) => setBook({ ...book, reading: e.target.value })}
          />
        </label>

        <label htmlFor="cover">
          Cover
          <input
            id="cover"
            type="text"
            value={book.cover}
            onChange={(e) => setBook({ ...book, cover: e.target.value })}
          />
        </label>

        <label htmlFor="status">
          Statut *:
          <select
            id="status"
            value={book.status}
            onChange={(e) => setBook({ ...book, status: e.target.value })}
          >
            <option> </option>
            <option value="Lu">Lu</option>
            <option value="En cours">En cours</option>
            <option value="À lire">À lire</option>
          </select>
        </label>

        <input type="submit" value="Submit" />
      </form>
    </>
  );
}

export default CreateBook;
