import React, { useEffect, useState } from "react";
import axios from "axios";

const Statistics = () => {
  const [bookList, setBookList] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/books").then((allBooks) => {
      setBookList(allBooks.data);
    });
  }, []);

  const reducer = (map, val) => {
    if (map[val] === null) {
      map[val] = 1;
    } else {
      ++map[val];
    }
    return map;
  };
  console.log(bookList.map((book) => book.country).reduce(reducer, {}));

  return (
    <>
      <table>
        <thead>
          <tr>
            <th>Title</th>
            <th>Author</th>
            <th>Parution</th>
            <th>Country</th>
            <th>genre</th>
            <th>moviment</th>
            <th>reading</th>
            <th>cover</th>
          </tr>
        </thead>
        <tbody>
          {bookList.map((book, key) =>
            book.country === "France" ? (
              <tr key={key}>
                <td>{book.title}</td>
                <td>{book.author}</td>
                <td>{book.parution}</td>
                <td>{book.country}</td>
                <td>{book.genre}</td>
                <td>{book.moviment}</td>
                <td>{book.reading}</td>
                <td>
                  <img src={book.cover} alt="" className="h-28" />
                </td>
              </tr>
            ) : null
          )}
        </tbody>
      </table>
    </>
  );
};

export default Statistics;
